  val bestYet = List(
    BitVector.fromValidHex("").take(14272),
    BitVector.fromValidHex("").take(14960),
    BitVector.fromValidHex("").take(13995)
  ).traverse(bits => fitness(Program.parse(bits)).map(score => ScoredIndividual(bits,score)))
  //val startingPop = bestYet.map(best => List.fill(100)(best))
  val startingPop: IO[List[ScoredIndividual[Double]]] = List.range(0,100).parTraverse{ i => 
    for {
      size <- randomIO.flatMap(_.betweenInt(5,100))
      bits <- randomIO.flatMap(_.nextBytes(size)).map(BitVector(_))
      indiv <- IO(Program.parse(bits))
      score <- fitness(indiv)
    } yield ScoredIndividual(bits,score)
  }.map(_.take(100)) //.flatMap(xs => bestYet.map(bests => bests ++ xs.drop(bests.size)))
  
  val refTheBest = Ref.of[IO,ScoredIndividual[Double]](ScoredIndividual(BitVector.empty,0.0))
  val tolerance = math.pow(10,-14)
  //val refWhenTest = Ref.of[IO,Deadline](Deadline.now + 5.seconds)
  val onBestBP = std.Backpressure[IO](std.Backpressure.Strategy.Lossy,1)
  def onBest(si: ScoredIndividual[Double])(using genetic: Genetic[Program]): IO[Unit] = 
    onBestBP.flatMap { backpressure => 
      for {
        f1 <- backpressure.metered({ 
              IO.sleep(5.seconds) *>
                (for {
                  ref <- refTheBest
                  currentBest <- ref.get
                  _ <- IO.println("!!!!!!!!! NEW BEST !!!!!!!!")
                  p <- IO(genetic.fromBits(si.indiv).value)
                  _ <- test(p,1)
                  _ <- IO.println("!!!!!!!!!!!!!!!!!!!!!!!!!!")
                } yield ())
              }).start
        res1 <- f1.joinWithNever
      } yield ()
    }

  // evolve n generations
  def evolve( n: Int, 
              numParallel: Int, 
              printEvery: Int = 10, 
              maxTarget: Option[Double] = None,
              updateBest: ScoredIndividual[Double] => IO[Unit] = si => IO.unit)
              (implicit ord: cats.kernel.Order[Double], ring: spire.algebra.Ring[Double], randbetween: RandBetween[Double])
              :IO[List[ScoredIndividual[Double]]] =
    std.Supervisor[IO].use{ supervisor =>
      for { 
          rand <- randomIO
          pop <- startingPop
          evolvedPop <- Evolver.throttled(1).flatMap(_.evolveN(fitness)(pop,n,numParallel, printEvery,maxTarget,updateBest)(using Program.geneticJoyBool,supervisor,rand,ord,ring,randbetween))
      } yield evolvedPop
    }

  def test(candidate: Program, numTests:Int = numTestCases): IO[Unit] = for {
    cases <- generateTestCases(numTests)
    output <- cases.traverse(testcase => for {
        _ <- IO.println("-----")
        _ <- IO.println(testcase.initialState)
        _ <- IO.println(testcase.expectedFinalState)
        _ <- IO.println("-----")
        output <- candidate(testcase.initialState)
        _ <- IO.println(output)
        _ <- IO.println("================================")
      } yield ()      
    )

  } yield ()

  def fx(candidate: Program): Int => Int => IO[Int] = lhs => rhs => {
    val psi = ProgramState().copy(input = BitVector.fromInt(lhs) ++ BitVector.fromInt(rhs))
    candidate(psi).map(_.output.toInt())
  }

  extension(p: Program)
    def addMod31(lhs: Int, rhs: Int): Int = fx(p)(lhs)(rhs).unsafeRunSync()(cats.effect.unsafe.implicits.global)

  //override protected def blockedThreadDetectionEnabled = true
  def run: IO[Unit] = evolve(100000,numParallel = 1, printEvery = 20, maxTarget = Some(maxPossibleScore), updateBest = onBest).as(())

  def hamming(lhs: Int, rhs: Int): Int = java.lang.Integer.bitCount(lhs ^ rhs)
  extension(i: Int)
    def bitCount: Int = java.lang.Integer.bitCount(i)